import React, { useState } from 'react';
import axios from 'axios';
import './homepage.css'

function HomePage(props) {
    const [cityName, setCityName] = useState('');
    const [weatherData, setWeatherData] = useState({});

    const handleFetchData = async () => {        
        try {
            const response = await axios.post('http://localhost:8000/api/weather' , { city: cityName })
            setWeatherData(response.data)

        } catch (error) {
            console.log(error)
        }
    }
    console.log(weatherData);

    return (
        <div className='wrapper'>
            <div>
            </div>

            <input type='text' className='text-input' placeholder='City Name' onChange={(e) => setCityName(e.target.value)}/>
            <button className='search-button' onClick={() => handleFetchData()}/>
        </div>
    );
}

export default HomePage;
