import React, { useState } from 'react';
import axios from 'axios';
import './homepage.css'

function HomePage(props) {
    const [cityName, setCityName] = useState('');
    const [weatherData, setWeatherData] = useState({});

    const handleFetchData = async () => {
        try {
            const response = await axios.post('http://localhost:8000/api/weather', { city: cityName })
            setWeatherData(response.data)

        } catch (error) {
            console.log(error)
        }
    }
    console.log(weatherData);

    return (
        <div className='wrapper'>
            <div className='container'>
                <div className='text-input-container'>
                    <div className='text-container'>
                        <h1 className='text'>Step into WeatherWise, your personal weather companion. Get instant access to weather forecasts, so you're always one step ahead of changing conditions</h1>
                    </div>
                    <div className='input-container'>
                        <input type='text' className='input' placeholder='City Name' onChange={(e) => setCityName(e.target.value)} />
                        <button className='button' onClick={() => handleFetchData()} />
                    </div>
                </div>
                <div className='weather-container'>

                </div>
            </div>
        </div>
    );
}

export default HomePage;
