import React, { useState } from 'react';
import axios from 'axios';
import './homepage.css'
import searchIcon from '../assets/search.png'

function HomePage(props) {
    const [cityName, setCityName] = useState('');
    const [weatherData, setWeatherData] = useState([]);

    const handleFetchData = async () => {
        try {
            const response = await axios.post('http://localhost:8000/api/weather', { city: cityName })
            setWeatherData(response.data)

        } catch (error) {
            console.log(error)
        }
    }

    return (
        <div className='wrapper'>
            <div className='container'>
                <div className='text-input-container'>
                    <div className='text-container'>
                        <h1 className='header'>WeatherWise - your personal weather companion</h1>
                        <h1 className='text'> Get instant access to weather forecasts, so you're always one step ahead of changing conditions.</h1>
                    </div>
                    <div className='input-container'>
                        <input type='text' className='input' placeholder='City Name' onChange={(e) => setCityName(e.target.value)} />
                        <button className='button' onClick={() => handleFetchData()} >
                            <img className='search-icon' src={searchIcon} alt='search'/>
                        </button>
                    </div>
                </div>
                <div className='weather-container'>
                    {weatherData? (
                        <h1>{weatherData.location.name}</h1>
                    ):
                    (
                        <h1>hi</h1>
                    )
                    }
                </div>
            </div>
        </div>
    );
}

export default HomePage;
